function ViewProgram(props) {
    const [latestProgram, setLatestProgram] = useState(null);

    const initialPrograms = { list: [], latest: null };

    useEffect(() => {
        loadPrograms();
    }, [currentClient]);

    const programsReducer = (state, action) => {
        return { ...state, list: action, latest: action[0] };
    };
    const [programs, dispatch] = useReducer(programsReducer, initialPrograms);

    async function loadPrograms() {
        if (props.currentClient) {
            try {
                await listPrograms(currentClient).then((data) => {
                    dispatch(data);
                }).then(() => {
                    if (programs.length > 0) {
                        //setLatestProgram(programs[0]);
                    }
                    else {
                        setLatestProgram(null);
                    }
                });
            }
            catch (e) {
                onError(e);

            }
        }
        else {
            dispatch([]);
            setLatestProgram(null);
        }
    }

    /*componentDidMount = async () => {

    }*/
    return (
        <React.Fragment>
            <Link to="/program/create">
                Add Program
            </Link>
            <br />
            {(currentClient) ? (<ViewGoals program={programs.latest} />) : null}
        </React.Fragment>
    )
}

const mapStateToProps = (state) => ({
    currentClient: state.client.currentClient,
});

export default connect(mapStateToProps, mapDispatchToProps)(ViewProgram);
