function ViewProgram() {
    const { currentClient } = useAppContext();
    const [latestProgram, setLatestProgram] = useState(null);

    const initialPrograms = { list: [], latest: null };

    const [programs, dispatch] = useReducer(programsReducer, initialPrograms);

    const programsReducer = (state, action) => {
        /*
        switch (action.type) {
            case 'DO_TODO':
                return state.map(todo => {
                    if (todo.id === action.id) {
                        return { ...todo, complete: true };
                    } else {
                        return todo;
                    }
                });
            case 'UNDO_TODO':
                return state.map(todo => {
                    if (todo.id === action.id) {
                        return { ...todo, complete: false };
                    } else {
                        return todo;
                    }
                });
            default:
                return state;
        }
        */
        return { ...state, programs: action, latest: action[0] };
    };


    useEffect(() => {
        console.log(currentClient);
        loadPrograms();
    }, [currentClient]);


    async function loadPrograms() {
        if (currentClient) {
            try {
                await listPrograms(currentClient).then((data) => {
                    console.log(data);
                    programsReducer(data);
                    console.log(programs);
                }).then(() => {
                    if (programs.length > 0) {
                        //setLatestProgram(programs[0]);
                    }
                    else {
                        setLatestProgram(null);
                    }
                });
            }
            catch (e) {
                onError(e);

            }
        }
        else {
            dispatch([]);
            setLatestProgram(null);
        }
    }

    /*componentDidMount = async () => {

    }*/
    return (
        <React.Fragment>
            <Link to="/program/create">
                Add Program
            </Link>
            <button>store program</button>
            <br />
            {(currentClient) ? (<ViewGoals program={latestProgram} />) : null}
        </React.Fragment>
    )
}

export default ViewProgram
