import React, { useState, useEffect } from "react";
//import { BrowserRouter as Router } from "react-router-dom";
import Routes from "../Routes";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
//import { LinkContainer } from "react-router-bootstrap";
import { AppContext } from "../libs/contextLib";
import { Auth } from "aws-amplify";
import { useHistory } from "react-router-dom";
import { onError } from "../libs/errorLib";
import AppBar from "@material-ui/core/AppBar";
import { Link } from "react-router-dom";
import ListItemText from "@material-ui/core/ListItemText";
import Typography from "@material-ui/core/Typography";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import MenuIcon from "@material-ui/icons/Menu";
import Divider from "@material-ui/core/Divider";
import Drawer from "@material-ui/core/Drawer";
import Hidden from "@material-ui/core/Hidden";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import TableChartIcon from "@material-ui/icons/TableChart";
import HomeIcon from "@material-ui/icons/Home";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import CssBaseline from "@material-ui/core/CssBaseline";
import { listClients } from "../api";
import FormControl from '@material-ui/core/FormControl';
import Select from "@material-ui/core/Select";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from "@material-ui/core/MenuItem";
//import store from "../store";
import "bootstrap/dist/css/bootstrap.min.css";

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  grow: {
    flexGrow: "1",
  },
  drawer: {
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up("sm")]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

function App(props) {
  const { window } = props;
  const theme = useTheme();
  const [clients, setClients] = useState([]);
  const [currentClient, setCurrentClient] = useState("");
  const classes = useStyles();
  const history = useHistory();
  //const [isAuthenticating, setIsAuthenticating] = useState(true);
  const [isAuthenticated, userHasAuthenticated] = useState(false);

  const [mobileOpen, setMobileOpen] = React.useState(false);

  useEffect(() => {
    onLoad();
  }, []);
  async function onLoad() {
    try {
      await Auth.currentSession();
      userHasAuthenticated(true);
    } catch (e) {
      if (e !== "No current user") {
        onError(e);
      }
    }
    //clients = await listClients();
    setClients(await listClients());
    //setIsAuthenticating(false);
  }

  async function handleLogout() {
    await Auth.signOut();
    userHasAuthenticated(false);
    history.push("/login");
  }
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  const drawer = isAuthenticated ? (
    <div>
      <div className={classes.toolbar} />
      <List>
        <ListItem>
          <ListItemIcon>
            <AccountCircleIcon />
          </ListItemIcon>
          <FormControl variant="outlined" className={classes.grow}>
            <InputLabel id="demo-simple-select-filled-label">Client</InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="client"
              value={currentClient}
              onChange={(e) => {
                setCurrentClient(e.target.value);
              }}
              label="Client"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              {clients.map((c) => (
                <MenuItem value={c.clientId}>{c.firstName}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </ListItem>
        <ListItem button key="Dashboard">
          <ListItemIcon>
            <HomeIcon />
          </ListItemIcon>
          <Link to="/">
            <ListItemText primary={"Dashboard"} />
          </Link>
        </ListItem>
        <ListItem button key="Program Block">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/program">
            <ListItemText primary={"Program Block"} />
          </Link>
        </ListItem>
        <ListItem button key="Sessions">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/sessions">
            <ListItemText primary={"Sessions"} />
          </Link>
        </ListItem>
        <ListItem button key="Profile">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/profile">
            <ListItemText primary={"Profile"} />
          </Link>
        </ListItem>
        <ListItem button key="Add Client">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/add-client">
            <ListItemText primary={"Add Client"} />
          </Link>
        </ListItem>
      </List>
      <Divider />
    </div>
  ) : null;
  const container =
    window !== undefined ? () => window().document.body : undefined;


  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="fixed" className={classes.appBar}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            className={classes.menuButton}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap className={classes.grow}>
            Speech Therapy Tracker
          </Typography>
          {isAuthenticated ? (
            <>
              <div>
                <Link to="/settings">
                  <ListItemText primary={"Settings"} className={classes.grow} />
                </Link>
              </div>
              <div>
                <Link onClick={handleLogout}>
                  <Typography
                    variant="button"
                    noWrap
                    className={classes.grow}
                    color="secondary"
                  >
                    Logout
                  </Typography>
                </Link>
              </div>
            </>
          ) : (
            <>
              <div>
                <Link to="/signup">
                  <ListItemText primary={"Signup"} />
                </Link>
              </div>
              <div>
                <Link to="/login">
                  <ListItemText primary={"Login"} />
                </Link>
              </div>
            </>
          )}
        </Toolbar>
      </AppBar>
      <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === "rtl" ? "right" : "left"}
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <>
        <AppContext.Provider value={{ isAuthenticated, userHasAuthenticated, currentClient }}>
          <main className={classes.content}>
            <div className={classes.toolbar} />
            <Routes />
          </main>
        </AppContext.Provider>
      </>
    </div>
  );
}

export default App;
