import React, { useState, useEffect } from 'react'
import { useAppContext } from "../libs/contextLib";
import { onError } from "../libs/errorLib";
import ReactTable from 'react-table'
import { listPrograms } from '../api';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';

import styled from 'styled-components'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

const Update = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

const Delete = styled.div`
    color: #ff0000;
    cursor: pointer;
`

/*
class UpdateGoal extends Component {
    updateUser = event => {
        event.preventDefault()
        window.location.href = `/program/update/${this.props.id}`
    }
    render() {
        const updateLink = `/program/update/${this.props.id}`;
        return (
            <Link to={updateLink}>
                Update
            </Link>
        )
    }
}
class DeleteGoal extends Component {
    deleteUser = event => {
        event.preventDefault()

        if (
            window.confirm(
                `Do you want to delete the goal ${this.props.name} permanently?`,
            )
        ) {
            api.deleteGoalById(this.props.id)
            window.location.reload()
        }
    }

    render() {
        return <Delete onClick={this.deleteUser}>Delete</Delete>
    }
}
*/
function ViewProgram() {
    const { currentClient } = useAppContext();
    const [client, setClient] = useState(null);
    const [programs, setPrograms] = useState([]);
    const [goals, setGoals] = useState([]);

    console.log(currentClient);
    setClient(currentClient);

    useEffect(() => {
        onLoad();
    }, []);

    async function onLoad() {
        try {
            if (client != null) {
                setPrograms(await listPrograms(client));
                console.log(programs);
            }
        } catch (e) {
            if (e !== "No programs") {
                onError(e);
            }
        }

    }

    /*componentDidMount = async () => {

    }*/
    return (
        <React.Fragment>
            <Link to="/program/create">
                Add Program
                </Link>
            <br />
            {(programs.length > 0) ? (
                <TableContainer component={Paper}>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>
                                    {"Start Date: "}

                                    <Moment format="MMMM D YYYY">
                                        {programs[0].startDate}
                                    </Moment>
                                </TableCell>
                                <TableCell></TableCell>
                                <TableCell></TableCell>
                                <TableCell colSpan="2">
                                    <Link to="/program/create">
                                        Add Goal
                            </Link>
                                </TableCell>
                            </TableRow>
                            <TableRow>
                                <TableCell></TableCell>
                                <TableCell align="center">Goal</TableCell>
                                <TableCell align="center">Substeps</TableCell>
                                <TableCell></TableCell>
                                <TableCell></TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {goals.map((row, index) => (
                                <TableRow key={row.name} style={{ textAlign: "top" }}>
                                    <TableCell align="left">Goal {index + 1}</TableCell>
                                    <TableCell align="left">{row.name}</TableCell>
                                    <TableCell align="left">1. {row.substep1}<br />2. {row.substep2}<br />3. {row.substep3}</TableCell>
                                    <TableCell align="right"></TableCell>
                                    <TableCell align="right"></TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            ) : null
            }
        </React.Fragment>
    )
}

export default ViewProgram
