import React, { useState, useEffect } from 'react';
import { connect } from "react-redux";
import { onError } from "../../libs/errorLib";
import AddGoals from './AddGoals';
import { listGoals } from '../../api';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Moment from 'react-moment';
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
    grow: {
        flexGrow: "1",
    },
    formControl: {
        minWidth: 150,
    },
}));

function ViewGoals(props) {
    const classes = useStyles();
    const [goals, setGoals] = useState([]);
    const { currentProgram, currentClient } = props;

    const goalBank = [{
        id: "artic-1",
        description: "Given 20 sounds and a verbal prompt or model, STUDENT will articulate the sound(s) of / / at the isolation level with 80% accuracy in 4 out of 5 opportunities.",
    }];
    console.log(props);
    useEffect(() => {
        if (currentClient && currentProgram)
            loadGoals(currentProgram.programId);
    }, []);

    async function loadGoals(id) {
        //event.preventDefault();
        //setIsLoading(true);
        try {
            await listGoals(id).then((data) => {
                setGoals(data);
            });
            //clear form values after submit complete

        } catch (e) {
            console.log(e);
        }
    }



    /*componentDidMount = async () => {

    }*/
    return (
        (currentClient && currentProgram) ? (
            < div className={classes.grow} >
                <Paper style={{ padding: '1rem' }}>
                    <Typography variant="h5">
                        Start Date: {(currentProgram) ? (<Moment format="MMMM D, YYYY">{currentProgram.startDate}</Moment>) : ''}
                    </Typography>
                    <Container>
                        <Accordion>
                            <AccordionSummary
                                expandIcon={<ExpandMoreIcon />}
                            >
                                <Typography variant="h6">Current Goal Summary</Typography>
                            </AccordionSummary>
                            <AccordionDetails>
                                <Grid container spacing={1}>
                                    <Grid item className={classes.grow} >
                                        <TableContainer>
                                            <Table>
                                                <TableHead>
                                                    <TableRow>
                                                        <TableCell align="left">Goal</TableCell>
                                                        <TableCell align="left">Target</TableCell>
                                                    </TableRow>
                                                </TableHead>
                                                <TableBody>
                                                    {goals.map((goal) => (
                                                        <TableRow className={classes.grow}>
                                                            <TableCell align="left">{goal.longTermGoal}</TableCell>
                                                            <TableCell align="left">{goal.target}</TableCell>
                                                        </TableRow>
                                                    ))}
                                                </TableBody>
                                            </Table>
                                        </TableContainer>
                                    </Grid>
                                </Grid>
                            </AccordionDetails>
                        </Accordion>
                        <AddGoals program={currentProgram} loadGoals={loadGoals} />
                    </Container>
                </Paper>
            </div >) : null
    );
}

const mapStateToProps = (state) => ({
    currentClient: state.client.currentClient,
    currentProgram: state.prog.currentProgram,
});

export default connect(mapStateToProps)(ViewGoals);
