const initialState = {
  currentClient: {},
  clients: []
};

export default function (state = initialState, action) {
  switch (action.type) {
    case "SET_CURRENT_CLIENT":
      return {
        ...state,
        currentClient: action.payload,
      };
    case "CLEAR_CLIENT":
      return { currentClient: {} };
    case "SET_ALL_CLIENTS":
      console.log("set clients")
      return { clients: action.payload };
    default:
      return state;
  }
}
