import React, { Component } from 'react'
import api from '../api/program'

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class GoalInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            name: '',
            rating: '',
            time: '',
        }
    }

    handleChangeInputName = async event => {
        const name = event.target.value
        this.setState({ name })
    }

    handleChangeInputRating = async event => {
        const rating = event.target.validity.valid
            ? event.target.value
            : this.state.rating

        this.setState({ rating })
    }

    handleChangeInputTime = async event => {
        const time = event.target.value
        this.setState({ time })
    }

    handleIncludeGoal = async () => {
        const { name, substeps } = this.state
        const payload = { name, substeps }

        await api.insertGoal(payload).then(res => {
            window.alert(`Goal inserted successfully`)
            this.setState({
                name: '',
                substeps: '',
            })
        })
    }

    render() {
        const { name, substeps } = this.state
        return (
            <Wrapper>
                <Title>Create Goal</Title>

                <Label>Name: </Label>
                <InputText
                    type="text"
                    value={name}
                    onChange={this.handleChangeInputName}
                />

                <Label>Substep: </Label>
                <InputText
                    type="text"
                    lang="en-US"
                    value={substeps}
                    onChange={this.handleChangeInputRating}
                />


                <Button onClick={this.handleIncludeGoal}>Add Goal</Button>
                <CancelButton href={'/program'}>Cancel</CancelButton>
            </Wrapper>
        )
    }
}

export default GoalInsert
