import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import Routes from "../routes/Routes";
import { AppContext } from "../libs/contextLib";
import { Auth } from "aws-amplify";
import { useHistory } from "react-router-dom";
import { onError } from "../libs/errorLib";
import { Link } from "react-router-dom";
import ListItemText from "@material-ui/core/ListItemText";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Divider from "@material-ui/core/Divider";
import Drawer from "@material-ui/core/Drawer";
import Hidden from "@material-ui/core/Hidden";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import TableChartIcon from "@material-ui/icons/TableChart";
import HomeIcon from "@material-ui/icons/Home";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import CssBaseline from "@material-ui/core/CssBaseline";
import { listClients } from "../api";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import "bootstrap/dist/css/bootstrap.min.css";

import NavBar from "../components/NavBar";
import SideBar from "../components/SideBar";

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  grow: {
    flexGrow: "1",
  },
  drawer: {
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up("sm")]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

function App(props) {
  const { window } = props;
  const theme = useTheme();
  const [clients, setClients] = useState([]);
  const [currentClient, setCurrentClient] = useState("");
  const classes = useStyles();
  const history = useHistory();
  //const [isAuthenticating, setIsAuthenticating] = useState(true);
  const [isAuthenticated, userHasAuthenticated] = useState(false);

  const [mobileOpen, setMobileOpen] = React.useState(false);

  useEffect(() => {
    onLoad();
  }, []);
  async function onLoad() {
    try {
      await Auth.currentSession();
      userHasAuthenticated(true);
    } catch (e) {
      if (e !== "No current user") {
        onError(e);
      }
    }
    //clients = await listClients();
    setClients(await listClients());
    //setIsAuthenticating(false);
  }

  async function handleLogout() {
    await Auth.signOut();
    userHasAuthenticated(false);
    history.push("/login");
  }
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  const drawer = isAuthenticated ? (
    <div>
      <div className={classes.toolbar} />
      <List>
        <ListItem>
          <ListItemIcon>
            <AccountCircleIcon />
          </ListItemIcon>
          <FormControl variant="outlined" className={classes.grow}>
            <InputLabel id="demo-simple-select-filled-label">Client</InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="client"
              value={currentClient}
              onChange={(e) => {
                setCurrentClient(e.target.value);
              }}
              label="Client"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              {clients.map((c) => (
                <MenuItem key={c.clientId} value={c.clientId}>
                  {c.firstName}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </ListItem>
        <ListItem button key="Dashboard">
          <ListItemIcon>
            <HomeIcon />
          </ListItemIcon>
          <Link to="/">
            <ListItemText primary={"Dashboard"} />
          </Link>
        </ListItem>
        <ListItem button key="Program Block">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/program">
            <ListItemText primary={"Program Block"} />
          </Link>
        </ListItem>
        <ListItem button key="Sessions">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/sessions">
            <ListItemText primary={"Sessions"} />
          </Link>
        </ListItem>
        <ListItem button key="Profile">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/profile">
            <ListItemText primary={"Profile"} />
          </Link>
        </ListItem>
        <ListItem button key="Add Client">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/add-client">
            <ListItemText primary={"Add Client"} />
          </Link>
        </ListItem>
      </List>
      <Divider />
    </div>
  ) : null;
  const container =
    window !== undefined ? () => window().document.body : undefined;

  return (
    <div className={classes.root}>
      <CssBaseline />
      <NavBar mobileOpen={mobileOpen} setMobileOpen={setMobileOpen} />
      <SideBar isAuthenticated={isAuthenticated} />
      <>
        <AppContext.Provider
          value={{ isAuthenticated, userHasAuthenticated, currentClient }}
        >
          <main className={classes.content}>
            <div className={classes.toolbar} />
            <Routes />
          </main>
        </AppContext.Provider>
      </>
    </div>
  );
}
/*
//retrieve state from store
const mapStateToProps = (state) => ({
  ...state,
});

const mapDispatchToProps = (dispatch) => ({
  storeResults: (payload) => dispatch(storeResults(payload)),
  appendResults: (payload) => dispatch(appendResults(payload)),
  clearResults: () => dispatch(clearResults()),
});
*/
export default connect()(App);
