import React, { useEffect } from 'react';
import { connect } from "react-redux";
import { storeClient } from "../redux/actions/clientActions";
import { Link } from "react-router-dom";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import CssBaseline from '@material-ui/core/CssBaseline';
import Divider from '@material-ui/core/Divider';
import Drawer from '@material-ui/core/Drawer';
import MenuItem from "@material-ui/core/MenuItem";
import Hidden from '@material-ui/core/Hidden';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import TableChartIcon from '@material-ui/icons/TableChart';
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import HomeIcon from '@material-ui/icons/Home';
import { makeStyles, useTheme } from '@material-ui/core/styles';

import "bootstrap/dist/css/bootstrap.min.css";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

function SideBar(props) {

  const { window } = props;
  const classes = useStyles();
  const theme = useTheme();

  const handleDrawerToggle = () => {
    props.setMobileOpen(!props.mobileOpen);
  };



  const drawer = (props.auth.isAuthenticated) ? (
    <div>
      <div className={classes.toolbar} />
      <List>
        <ListItem>
          <ListItemIcon>
            <AccountCircleIcon />
          </ListItemIcon>
          <FormControl variant="outlined" className={classes.grow}>
            <InputLabel id="client-select-label">Client</InputLabel>
            <Select
              labelId="client-select-label"
              id="client"
              value={props.currentClient}
              onChange={(e) => {
                props.storeClient(e.target.value);
              }}
              label="Client"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              {props.clients.map((c) => (
                <MenuItem key={c.clientId} value={c.clientId}>
                  {c.firstName}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </ListItem>
        <ListItem button key="Dashboard">
          <ListItemIcon>
            <HomeIcon />
          </ListItemIcon>
          <Link to="/">
            <ListItemText primary={"Dashboard"} />
          </Link>
        </ListItem>
        <ListItem button key="Program Block">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/program">
            <ListItemText primary={"Program Block"} />
          </Link>
        </ListItem>
        <ListItem button key="Sessions">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/sessions">
            <ListItemText primary={"Sessions"} />
          </Link>
        </ListItem>
        <ListItem button key="Profile">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/profile">
            <ListItemText primary={"Profile"} />
          </Link>
        </ListItem>
        <ListItem button key="Add Client">
          <ListItemIcon>
            <TableChartIcon />
          </ListItemIcon>
          <Link to="/add-client">
            <ListItemText primary={"Add Client"} />
          </Link>
        </ListItem>
      </List>
      <Divider />
    </div>
  ) : null;

  const container = window !== undefined ? () => window().document.body : undefined;

  return (
    <div className={classes.root}>
      <CssBaseline />
      <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === "rtl" ? "right" : "left"}
            open={props.mobileOpen}
            onClose={handleDrawerToggle}
            className={classes.drawerPaper}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
    </div>
  );
}

const mapStateToProps = (state) => ({
  currentClient: state.client.currentClient,
  clients: state.client.clients,
  auth: state.auth,
});

const mapDispatchToProps = (dispatch) => ({
  storeClient: (payload) => dispatch(storeClient(payload)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SideBar);
