import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { createProgram } from "../api";
import { useAppContext } from "../libs/contextLib";

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

function CreateProgram() {
    const { currentClient } = useAppContext();
    const [startDate, setStartDate] = useState(null);

    async function handleSubmit(event){
        event.preventDefault();
        //setIsLoading(true);
        try {
            await createProgram({
                clientId: currentClient,
                startDate: startDate,
            }).then((data) => {
                window.alert("New Program/Block added");
                console.log(data);
            });
            //clear form values after submit complete

        } catch (e) {
            console.log(e);
        }
    }
    /*
    componentDidMount = async () => {

    }
    */
    return (
        <Wrapper>
            <Title>Add Program</Title>
            <Label>Start Date: </Label>
            <InputText
                type="date"
                id="startDate"
                value={startDate}
                style={{ maxWidth: '-moz-fit-content' }}
                onChange={(e)=>{setStartDate(e.target.value)}}
            />
            <Button onClick={handleSubmit}>
                Add
            </Button>
            <CancelButton>
                <Link to="/program" style={{ color: "white", textDecoration: "none" }}>Cancel</Link>
            </CancelButton>
        </Wrapper>
    )
}

export default CreateProgram
