import React, { useState } from 'react'
import { connect } from "react-redux";
import { loggedOut } from "../redux/actions/authActions";
import { Auth } from "aws-amplify";
import { Link, useHistory } from "react-router-dom";
import styled from 'styled-components'
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import Typography from "@material-ui/core/Typography";
import ListItemText from "@material-ui/core/ListItemText";
import { makeStyles } from "@material-ui/core/styles";


const Container = styled.div.attrs({
    className: 'container',
})``
const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
    root: {
        display: "flex",
    },
    grow: {
        flexGrow: "1",
    },
    drawer: {
        [theme.breakpoints.up("sm")]: {
            width: drawerWidth,
            flexShrink: 0,
        },
    },
    appBar: {
        [theme.breakpoints.up("sm")]: {
            width: `calc(100% - ${drawerWidth}px)`,
            marginLeft: drawerWidth,
        },
    },
    menuButton: {
        marginRight: theme.spacing(2),
        [theme.breakpoints.up("sm")]: {
            display: "none",
        },
    },
    // necessary for content to be below app bar
    toolbar: theme.mixins.toolbar,
    drawerPaper: {
        width: drawerWidth,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
}));

function NavBar(props) {
    const classes = useStyles();
    const history = useHistory();
    //const [isAuthenticated, userHasAuthenticated] = useState(false);

    const handleDrawerToggle = () => {
        this.props.setMobileOpen(!this.props.mobileOpen);
    };
    async function handleLogout() {
        await Auth.signOut();
        //userHasAuthenticated(false);

        history.push("/login");
    }
    return (
        <Container>
            <AppBar position="fixed" className={classes.appBar}>
                <Toolbar>
                    <IconButton
                        color="inherit"
                        aria-label="open drawer"
                        edge="start"
                        onClick={handleDrawerToggle}
                        className={classes.menuButton}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" noWrap className={classes.grow}>
                        Speech Therapy Tracker
                        </Typography>
                    {props.isAuthenticated ? (
                        <>
                            <div>
                                <Link to="/settings">
                                    <ListItemText primary={"Settings"} className={classes.grow} />
                                </Link>
                            </div>
                            <div>
                                <Link onClick={handleLogout}>
                                    <Typography
                                        variant="button"
                                        noWrap
                                        className={classes.grow}
                                        color="secondary"
                                    >
                                        Logout
                    </Typography>
                                </Link>
                            </div>
                        </>
                    ) : (
                            <>
                                <div>
                                    <Link to="/signup">
                                        <ListItemText primary={"Signup"} />
                                    </Link>
                                </div>
                                <div>
                                    <Link to="/login">
                                        <ListItemText primary={"Login"} />
                                    </Link>
                                </div>
                            </>
                        )}
                </Toolbar>
            </AppBar>
        </Container>
    )
}

const mapStateToProps = (state) => ({
    isAuthenticated: state.isAuthenticated,
});

const mapDispatchToProps = (dispatch) => ({
    loggedOut: () => dispatch(loggedOut()),
});


export default connect(mapStateToProps, mapDispatchToProps)(NavBar);
