import React, { Component } from 'react'
import api from '../api/program'
import { Link } from 'react-router-dom'

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class GoalUpdate extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            name: '',
            rating: '',
            time: '',
        }
    }

    handleChangeInputName = async event => {
        const name = event.target.value
        this.setState({ name })
    }

    handleChangeInputRating = async event => {
        const rating = event.target.validity.valid
            ? event.target.value
            : this.state.rating

        this.setState({ rating })
    }

    handleChangeInputTime = async event => {
        const time = event.target.value
        this.setState({ time })
    }

    handleUpdateGoal = async () => {
        const { id, name, substeps } = this.state
        const payload = { name, substeps }
        await api.updateGoalById(id, payload).then(res => {
            window.alert(`Goal updated successfully`)
            this.setState({
                name: '',
                substeps: [],
            })
        })
    }
    componentDidMount = async () => {
        const { id } = this.state
        const goal = await api.getGoalById(id)
        //console.log(goal.data.data.substeps)
        this.setState({
            name: goal.data.data.name,
            substeps: goal.data.data.substeps,
        })
    }
    render() {
        const { name, substeps} = this.state
        console.log(substeps)
        return (
            <Wrapper>
                <Title>Update Goal</Title>

                <Label>Name: </Label>
                <InputText
                    type="text"
                    value={name}
                    onChange={this.handleChangeInputName}
                />

                <Button onClick={this.handleUpdateGoal}>
                        Update Program
                </Button>
                <CancelButton>
                    <Link to="/program" style={{color: "white",textDecoration: "none"}}>
                        Go Back
                    </Link>
                </CancelButton>
            </Wrapper>
        )
    }
}

export default GoalUpdate
