function ViewProgram() {
    const { currentClient } = useAppContext();
    const [programs, setPrograms] = useState([]);
    const [goals, setGoals] = useState([]);
    const [latestProgram, setLatestProgram] = useState('');


    useEffect(() => {
        onLoad();
    }, [currentClient]);


    async function onLoad() {
        try {
            if (currentClient != '') {
                setPrograms(await listPrograms(currentClient)).then(setLatestProgram(programs[0].programId));
                console.log(programs);
                //setLatestProgram(programs[0].programId);
            }
        } catch (e) {
            if (e !== "No programs") {
                onError(e);
            }
        }

    }

    /*componentDidMount = async () => {

    }*/
    return (
        <AppContext.Provider value={{ latestProgram }}>
            <React.Fragment>
                <Link to="/program/create">
                    Add Program
                </Link>
                <br />
                {(programs.length > 0) ? (
                    <TableContainer component={Paper}>
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>
                                        {"Start Date: "}

                                        <Moment format="MMMM D YYYY">
                                            {programs[0].startDate}
                                        </Moment>
                                    </TableCell>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell colSpan="2">
                                        <Link to="/goals">
                                            Add Goal
                                    </Link>
                                    </TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {(goals.length > 0) ? (
                                    <TableRow>
                                        <TableCell></TableCell>
                                        <TableCell align="center">Goal</TableCell>
                                        <TableCell align="center">Substeps</TableCell>
                                        <TableCell></TableCell>
                                        <TableCell></TableCell>
                                    </TableRow>) : <center>No goals added...</center>
                                }
                                {goals.map((row, index) => (
                                    <TableRow key={row.name} style={{ textAlign: "top" }}>
                                        <TableCell align="left">Goal {index + 1}</TableCell>
                                        <TableCell align="left">{row.name}</TableCell>
                                        <TableCell align="left">1. {row.substep1}<br />2. {row.substep2}<br />3. {row.substep3}</TableCell>
                                        <TableCell align="right"></TableCell>
                                        <TableCell align="right"></TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                ) : null
                }
            </React.Fragment>
        </AppContext.Provider>
    )
}

export default ViewProgram
