import React, { Component } from 'react'
import api from '../api/program'
import { Link } from 'react-router-dom'

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class GoalUpdate extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            name: '',
            substeps: [],
        }
    }

    handleChangeInputName = async event => {
        const name = event.target.value
        this.setState({ name })
    }
    handleChangeInputSubsteps = (index) = event => {
        let newArr = [this.substeps]; 
        console.log(event.target.value)
        newArr[index] = event.target.value
        this.setState({ substeps: newArr })
    }
    handleUpdateGoal = async () => {
        const { id, name, substeps } = this.state
        const payload = { name, substeps }
        await api.updateGoalById(id, payload).then(res => {
            window.alert(`Goal updated successfully`)
            this.setState({
                name: '',
                substeps: [],
            })
        })
    }
    componentDidMount = async () => {
        const { id } = this.state
        const goal = await api.getGoalById(id)
        this.setState({
            name: goal.data.data.name,
            substeps: goal.data.data.substeps,
        })
    }
    render() {
        const { name, substeps} = this.state
        const old_substeps = substeps
        console.log(this.substeps)
        return (
            <Wrapper>
                <Title>Update Goal</Title>

                <Label>Name: </Label>
                <InputText
                    type="text"
                    value={name}
                    onChange={this.handleChangeInputName}
                />
                {old_substeps.map((row, index) => (
                    <React.Fragment>
                        <Label>Substep {index+1}: </Label>
                        <InputText
                            type="text"
                            value={row}
                        />
                    </React.Fragment>
                ))}
                <Button onClick={this.handleUpdateGoal}>
                        Update Program
                </Button>
                <CancelButton>
                    <Link to="/program" style={{color: "white",textDecoration: "none"}}>
                        Go Back
                    </Link>
                </CancelButton>
            </Wrapper>
        )
    }
}

export default GoalUpdate
