import { LOGGED_IN, LOGGED_OUT, USER_LOADING } from "../actions/types";

const initialState = {
  isAuthenticated: true,
  loading: false,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case LOGGED_IN:
      return {
        ...state,
        isAuthenticated: true,
        loading: false,
      };
    case LOGGED_OUT:
      return {
        ...state,
        isAuthenticated: false,
      };
    case USER_LOADING:
      return {
        ...state,
        loading: true,
      };
    default:
      return state;
  }
}
