import React, { Component } from 'react'
import api1 from '../api/sessions'
import api2 from '../api/program'
import { Link } from 'react-router-dom'
import { Switch, Select, MenuItem } from '@material-ui/core'
import { API } from "aws-amplify";

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class ClientInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            firstName: '',
            lastName: '',
            date: null,
        }
    }
    handleChange = event => {
        const id = event.target.id;
        this.setState({
            [id]: event.target.value
        });
    };
    handleSubmit = async event => {
        event.preventDefault();
        //setIsLoading(true);
        try {
            await this.createAID({
                firstName: this.state.firstName,
                lastName: this.state.lastName,
                dob: this.state.dob,
            });
            //clear form values after submit complete
            this.setState({
                firstName: "",
                lastName: "",
                dob: null,
            });
        } catch (e) {
            console.log(e);
        }
    }
    createAID = content => {
        return API.post("stt", "/client", {
            body: content
        });
    }
    componentDidMount = async () => {
        await api2.getAllGoals().then(response => {
            this.setState({
                goals: response.data.data
            })
        })
    }
    render() {
        const { firstName, lastName, dob } = this.state
        return (
            <Wrapper>
                <Title>Add Client</Title>
                <Label>First Name: </Label>
                <InputText
                    type="text"
                    value={firstName}
                    style={{ maxWidth: '-moz-fit-content' }}
                    onChange={this.handleChange}
                />
                <Label>Last Name: </Label>
                <InputText
                    type="text"
                    value={lastName}
                    style={{ maxWidth: '-moz-fit-content' }}
                    onChange={this.handleChange}
                />
                <Label>Date of Birth: </Label>
                <InputText
                    type="date"
                    value={dob}
                    style={{ maxWidth: '-moz-fit-content' }}
                    onChange={this.handleChange}
                />
                <Button onClick={this.handleSubmit}>
                    Add
                </Button>
                <CancelButton>
                    <Link to="/dashboard" style={{ color: "white", textDecoration: "none" }}>Cancel</Link>
                </CancelButton>
            </Wrapper>
        )
    }
}

export default ClientInsert
