import React, { useState } from 'react'
import { updateGoal } from "../../api";
import { Link } from 'react-router-dom'
import { useFormFields } from "../../libs/hooksLib";
import FormControl from '@material-ui/core/FormControl';
import Select from "@material-ui/core/Select";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from "@material-ui/core/MenuItem";


function UpdateGoal(props) {
    console.log(props);
    const [fields, handleFieldChange] = useFormFields({
        longTermGoal: '',
        subGoals: [],
        target: '',
    });
    const [category, setCategory] = useState('');

    async function handleInputChange(event) {
        const name = event.target.value
        this.setState({ name })
    }

    handleUpdateGoal = async () => {
        const payload = {
            category: category,
            longTermGoal: fields.longTermGoal,
            target: fields.target,
        };
        await updateGoal(id, payload).then(res => {
            window.alert(`Goal updated successfully`);
        })
    }
    return (
        <Wrapper>
            <Title>Update Goal</Title>

            <FormControl variant="outlined" className={classes.formControl}>
                <InputLabel id="goal-category-label">Category</InputLabel>
                <Select
                    labelId="goal-category-label"
                    id="category"
                    value={category}
                    onChange={handleCategoryChange}
                    label="Category"
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value="articulation">
                        <em>Articulation</em>
                    </MenuItem>
                    <MenuItem value="expressive">
                        <em>Expressive Communication</em>
                    </MenuItem>
                    <MenuItem value="receptive">
                        <em>Receptive Communication</em>
                    </MenuItem>
                    <MenuItem value="social_skills">
                        <em>Social Skills</em>
                    </MenuItem>
                </Select>
            </FormControl>

            <Label>Goal: </Label>
            <InputText
                type="text"
                value={name}
                onChange={handleFieldChange}
            />
            <Label>Substep 1: </Label>
            <InputText
                type="text"
                defaultValue={substep1}
                onChange={handleFieldChange}
            />
            <Label>Substep 2: </Label>
            <InputText
                type="text"
                defaultValue={substep2}
                onChange={handleFieldChange}
            />
            <Label>Substep 3: </Label>
            <InputText
                type="text"
                defaultValue={substep3}
                onChange={this.handleChangeInputSubstep3}
            />
            <Button onClick={handleUpdateGoal}>
                Update Program
                </Button>
            <CancelButton>
                <Link to="/program" style={{ color: "white", textDecoration: "none" }}>
                    Go Back
                </Link>
            </CancelButton>
        </Wrapper>
    )
}

export default UpdateGoal
