function ViewProgram() {
    const { currentClient } = useAppContext();
    const [programs, setPrograms] = useState([]);
    const [goals, setGoals] = useState([]);
    const [latestProgram, setLatestProgram] = useState(null);


    useEffect(() => {
        console.log(currentClient);
        loadPrograms();
    }, [currentClient]);


    async function loadPrograms() {
        try {
            if (currentClient != '') {
                console.log(currentClient);
                setPrograms(await listPrograms(currentClient));
                if (programs.length > 0) {
                    console.log(programs);
                    setLatestProgram(programs[0]);
                }
                else {
                    setPrograms([]);
                    setLatestProgram(null);
                }
            }
            else {
                setPrograms([]);
                setLatestProgram(null);
            }
        } catch (e) {
            if (e !== "No programs") {
                onError(e);
            }
        }
    }

