const initialState = {
  currentClient: {},
  clients: []
};

export default function (state = initialState, action) {
  switch (action.type) {
    case "SET_CURRENT_CLIENT":
      return Object.assign({}, state, {
        currentClient: action.client,
      });
    case "CLEAR_CLIENT":
      return Object.assign({}, state, {
        currentClient: {},
      });
    case "SET_ALL_CLIENTS":
      return Object.assign({}, state, {
        clients: action.clients,
      });
    default:
      return state;
  }
}
