import { SET_PROGRAMS, SET_CURRENT_PROGRAM } from "../actions/types";

const initialState = {
  programs: [],
  currentProgram: {},
};

export default function authReducer(state = initialState, action) {
  switch (action.type) {
    case SET_PROGRAMS:
      console.log("login");
      return Object.assign({}, state, {
        programs: action.programs,
      });
    case SET_CURRENT_PROGRAM:
      console.log("logout");
      return Object.assign({}, state, {
        currentProgram: action.program,
      });
    default:
      return state;
  }
}
