import { SET_GOALS, CLEAR_GOALS } from "../actions/types";

const initialState = {
  goals: [],
};

export default function goalsReducer(state = initialState, action) {
  switch (action.type) {
    case SET_GOALS:
      return Object.assign({}, state, {
        goals: action.goals,
      });
    case APPEND_GOAL:
      let goals = [...state];
      goals = goals.concat(action.goal);
      return Object.assign({}, state, {
        goals: goals,
      });
    case CLEAR_GOALS:
      return Object.assign({}, state, initialState);
    default:
      return state;
  }
}
