import React, { Component } from 'react'
import api from '../api/sessions'
import { Link } from 'react-router-dom'
import { Switch,Select, MenuItem } from '@material-ui/core'

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class SessionUpdate extends Component {
    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            date: null,
            goal: '',
            substep: '',
            completed: false,
            goals: [],
        }
    }

    handleChangeInputDate = async event => {
        const date = event.target.value
        this.setState({ date })
    }
    handleChangeInputGoal = async event => {
        const goal = event.target.value
        this.setState({ goal })
    }
    handleChangeInputSubstep = async event => {
        const substep = event.target.value
        this.setState({ substep })
    }
    handleChangeInputCompleted = async event => {
        const completed = event.target.value
        this.setState({ completed })
    }
    /*
    handleChangeInputSubsteps = (index) = event => {
        let newArr = [this.substeps]; 
        console.log(event.target.value)
        newArr[index] = event.target.value
        this.setState({ substeps: newArr })
    }
    */
    handleUpdateSession = async () => {
        const { id, date, goal, substep, completed } = this.state
        const payload = { date, goal, substep, completed }
        await api.updateSessionById(id, payload).then(res => {
            window.alert(`Session updated successfully`)
            this.setState({
                date: null,
                goal: '',
                substep: '',
                completed: false,
            })
        })
    }
    componentDidMount = async () => {        
        await api.getAllSessions().then(response => {  
            this.setState({
                goals: response.data.data
            })
        })
    }
    render() {
        const { date, goal, substep, completed, goals } = this.state
        return (
            <Wrapper>
                <Title>Update Session</Title>

                <Label>Date: </Label>
                <InputText
                    type="date"
                    value={date}
                    onChange={this.handleChangeInputDate}
                />
                <Label>Goal: </Label>
                <div style={{width:"100%"}}>
                    <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    style={{minWidth:'30%',width:'fit-content',width:'-moz-fit-content',paddingLeft:'5px'}}
                    value={goal}
                    onChange={this.handleChangeInputGoal}
                    >
                        {goals.map((item,index) => (
                        <MenuItem value={item.goal}>{item.goal}</MenuItem>
                        ))}
                    </Select>
                </div>
                <Label>Substep: </Label>
                <div style={{width:"100%"}}>
                    <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    style={{minWidth:'30%',width:'fit-content',width:'-moz-fit-content',paddingLeft:'5px'}}
                    value={goal}
                    onChange={this.handleChangeInputGoal}
                    >
                        {goals.map((item,index) => (
                        <MenuItem value={item.substep}>{item.substep}</MenuItem>
                        ))}
                    </Select>
                </div>
                <Label>
                    Completed: 
                    <Switch
                        checked={completed}
                        onChange={this.handleChangeInputCompleted}
                        color="primary"
                        name="completed"
                        inputProps={{ 'aria-label': 'primary checkbox' }}
                    />
                </Label>
                <Button onClick={this.handleUpdateSession}>
                        Update Session
                </Button>
                <CancelButton>
                    <Link to="/program" style={{color: "white",textDecoration: "none"}}>
                        Go Back
                    </Link>
                </CancelButton>
            </Wrapper>
        )
    }
}

export default SessionUpdate
