import React, { useState, useEffect, useContext } from 'react'
import { useAppContext } from "../../libs/contextLib";
import { onError } from "../../libs/errorLib";
import AddGoals from './AddGoals';
import { listGoals } from '../../api';
import { useFormFields } from "../../libs/hooksLib";
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Moment from 'react-moment';



function ViewGoals(props) {

    const [goals, setGoals] = useState([]);
    const program = (props.program) ? props.program : null;

    const goalBank = [{
        id: "artic-1",
        description: "Given 20 sounds and a verbal prompt or model, STUDENT will articulate the sound(s) of / / at the isolation level with 80% accuracy in 4 out of 5 opportunities.",
    }];

    useEffect(() => {
        loadGoals(program.programId);
    }, [program]);

    async function loadGoals(id) {
        //event.preventDefault();
        //setIsLoading(true);
        console.log(id);
        try {
            await listGoals(id).then((data) => {
                console.log(data);
                setGoals(data);
            });
            //clear form values after submit complete

        } catch (e) {
            console.log(e);
        }
    }



    /*componentDidMount = async () => {

    }*/
    return (
        <div style={{ flexGrow: 1 }}>
            <Paper style={{ padding: '1rem' }}>
                <Typography variant="h5">
                    Start Date: {(program) ? (<Moment format="MMMM D, YYYY">{program.startDate}</Moment>) : ''}
                </Typography>
                <Container>
                    <Accordion>
                        <AccordionSummary
                            expandIcon={<ExpandMoreIcon />}
                        >
                            <Typography variant="h6">Current Goal Summary</Typography>
                        </AccordionSummary>
                        <AccordionDetails>
                            <Grid container spacing={1}>
                                {goals.map((goal) => (
                                    <Grid item >
                                        <Card style={{ padding: 2, textAlign: 'center' }}>
                                            <CardContent>
                                                <TableContainer>
                                                    <Table>
                                                        <TableHead>
                                                            <TableRow>
                                                                <TableCell align="left">Goal</TableCell>
                                                                <TableCell align="left">Target</TableCell>
                                                            </TableRow>
                                                        </TableHead>
                                                        <TableBody>
                                                            <TableRow>
                                                                <TableCell align="left">{goal.longTermGoal}</TableCell>
                                                                <TableCell align="left">{goal.target}</TableCell>
                                                            </TableRow>
                                                        </TableBody>
                                                    </Table>
                                                </TableContainer>
                                            </CardContent>
                                        </Card>
                                    </Grid>
                                ))}
                            </Grid>
                        </AccordionDetails>
                    </Accordion>
                    <AddGoals program={program} loadGoals={loadGoals} />
                </Container>
            </Paper>
        </div>
    )
}

export default ViewGoals
