import React, { useState } from 'react'
import { updateGoal } from "../../api";
import { Link } from 'react-router-dom'
import { useFormFields } from "../../libs/hooksLib";
import FormControl from '@material-ui/core/FormControl';
import Select from "@material-ui/core/Select";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from "@material-ui/core/MenuItem";
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
    grow: {
        flexGrow: "1",
    },
    formControl: {
        minWidth: 150,
    },
}));


function UpdateGoal(props) {
    const classes = useStyles();
    console.log(props);
    const [fields, handleFieldChange] = useFormFields({
        longTermGoal: '',
        subGoals: [],
        target: '',
    });
    const [category, setCategory] = useState('');

    async function handleInputChange(event) {
        const name = event.target.value
        this.setState({ name })
    }

    async function handleUpdateGoal() {
        const payload = {
            category: category,
            longTermGoal: fields.longTermGoal,
            target: fields.target,
        };
        await updateGoal(id, payload).then(res => {
            window.alert(`Goal updated successfully`);
        })
    }
    const handleCategoryChange = (event) => {
        setCategory(event.target.value);
    }
    return (
        <Wrapper>
            <Title>Update Goal</Title>

            <FormControl variant="outlined" className={classes.formControl}>
                <InputLabel id="goal-category-label">Category</InputLabel>
                <Select
                    labelId="goal-category-label"
                    id="category"
                    value={category}
                    onChange={handleCategoryChange}
                    label="Category"
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value="articulation">
                        <em>Articulation</em>
                    </MenuItem>
                    <MenuItem value="expressive">
                        <em>Expressive Communication</em>
                    </MenuItem>
                    <MenuItem value="receptive">
                        <em>Receptive Communication</em>
                    </MenuItem>
                    <MenuItem value="social_skills">
                        <em>Social Skills</em>
                    </MenuItem>
                </Select>
            </FormControl>

            <InputLabel>Goal: </InputLabel>
            <InputText
                type="text"
                value={name}
                onChange={handleFieldChange}
            />
            <InputLabel>Substep 1: </InputLabel>
            <InputText
                type="text"
                defaultValue={substep1}
                onChange={handleFieldChange}
            />
            <InputLabel>Substep 2: </InputLabel>
            <InputText
                type="text"
                defaultValue={substep2}
                onChange={handleFieldChange}
            />
            <InputLabel>Substep 3: </InputLabel>
            <InputText
                type="text"
                defaultValue={substep3}
                onChange={this.handleChangeInputSubstep3}
            />
            <Button onClick={handleUpdateGoal}>
                Update Program
            </Button>
            <Button>
                <Link to="/program" style={{ color: "white", textDecoration: "none" }}>
                    Go Back
                </Link>
            </Button>
        </Wrapper>
    )
}

export default UpdateGoal
